var NewRow = {};

NewRow.ToolTracked = "T" + me.SelectedToolToTrack;

// get time in cut for tool being tracked
var values = Things["ToolTracking"].CreateValues();

values.ToolNo = me.SelectedToolToTrack; //INTEGER [Primary Key]

var params = {
	tags: undefined /* TAGS */,
	values: values /* INFOTABLE*/,
	source: me.name /* STRING */,
	query: undefined /* QUERY */,
	maxItems: undefined /* NUMBER */
};

// result: INFOTABLE
var SelectedToolData = Things["ToolTracking"].QueryDataTableEntries(params);

var tableLength = SelectedToolData.rows.length;

if (tableLength > 0){
    NewRow.TimeInCut = (SelectedToolData.rows[0].TimeInCut)/60;
    if (SelectedToolData.rows[0].EstToolLife > 0){
        NewRow.ToolLifePercent = SelectedToolData.rows[0].TimeInCut / SelectedToolData.rows[0].EstToolLife;
    }
    else{
        NewRow.ToolLifePercent = 0;
    }
}
else{
    NewRow.TimeInCut = 0;
    NewRow.ToolLifePercent = 0;
}

// get data for last tool changed
var params = {
	maxItems: undefined /* NUMBER */
};

// result: INFOTABLE
var ToolUsageRecords = Things["ToolTracking"].GetDataTableEntries(params);

var params = {
	sortColumn: "LastChange" /* STRING */,
	t: ToolUsageRecords /* INFOTABLE */,
	ascending: false /* BOOLEAN */
};

// result: INFOTABLE
var ToolUsageRecords = Resources["InfoTableFunctions"].Sort(params);

var tableLength = ToolUsageRecords.rows.length;

if (tableLength > 0){
    NewRow.LastToolChangeDate = ToolUsageRecords.rows[0].LastChange;
	NewRow.LastToolChanged = "T" +  ToolUsageRecords.rows[0].ToolNo;
}

result.AddRow(NewRow);

